bash -c 'for i in {1..20}; do clear; echo -e "\033[0;40;32m\n$(printf " %.0s" {1..15})\033[1;35mY\033[0;40;32m$(printf " %.0s" {1..15})"; for r in {1..8}; do printf "\n%*s" $((16-r)) ""; c=$((RANDOM % (2*r))); for x in $(seq 0 $((2*r-1))); do if [ "$x" -eq "$c" ]; then f=$((31+RANDOM%3)); printf "\033[%d;1m*\033[0;40;32m" $f; else printf "X"; fi; done; done; echo -e "\n$(printf " %.0s" {1..31})\033[0m\n$(printf " %.0s" {1..8})Merry Xmas !!!\n\n"; sleep 0.5; done'
